Array.prototype.swap=function(i,j){   //  
    var tmp=this[i];
    this[i]=this[j];
    this[j]=tmp;
};

Array.prototype.heapSort=function(f){            //  
    var fn=f;;
    if(f==null){
        fn=function(a,b){return a-b;}
    }

    var that=this;
    buildHeap();
    for(var i=that.length-1;i>0;i--){
        that.swap(0,i);
        heapAdjust(0,i);
    }
    return that;
    function heapAdjust(i,j){
        var M=i;
        var left=i*2+1;
        var right=i*2+2;
        if(left<j&&fn(that[M],that[left])<0){
            M=left;
        }
        if(right<j&&fn(that[M],that[right])<0){
            M=right;
        }
        if(M!=i){
            that.swap(i,M);
            heapAdjust(M,j);
        }
    }
    function buildHeap(){
        for(var i=Math.floor(that.length/2)-1;i>=0;i--){
            heapAdjust(i,that.length);
        }
    }
}
