Array.prototype.quickSort = function(f) { //??????  
    var fn = f;;
    if (f == null) {
        fn = function(a, b) {
            return a > b;
        }
    }
    var arr = this;
    if (arr.length <= 1) {
        return arr;
    }
    var index = Math.floor(arr.length / 2);
    //   var pivot = arr.splice(index,1)[0];
    var pivot = arr[index];
    var left = [];
    var right = [];
    for (var i = 0; i < arr.length; i++) {
        if (i == index) {
            continue;
        }
        if (fn(arr[i], pivot)) {
            right.push(arr[i])
        } else {
            left.push(arr[i])
        }
    }
    return left.quickSort(fn).concat([pivot], right.quickSort(fn));
}

