$(document).on({  //
    dragleave:function(e){    //
        e.preventDefault();
    },
    drop:function(e){  //
        e.preventDefault();
    },
    dragenter:function(e){    //
        e.preventDefault();
    },
    dragover:function(e){    //
        e.preventDefault();
    }
});
var box = document.getElementById('drop_area'); //
box.addEventListener("drop",function(e){
    e.preventDefault(); //
    var fileList = e.dataTransfer.files; //
    console.log(fileList)
    //
    if(fileList.length == 0){
        return false;
    }
    //APK
   if(fileList[0].name.substring(fileList[0].name.lastIndexOf('.')+1).toLowerCase()!=='apk'){
        alert("APK");
        return false;
    }
    var Form=new FormData();
    Form.append('file',fileList[0]);
    $.ajax({
        url: config.api.UPLOAD_APK.url,
        type: "POST",
        data: Form,
        processData: false,  // jQuery
        contentType: false   // jQueryContent-Type
    }).done(function (data) {
        if(data.status===0){
            Message.show(<span><i className="glyphicon glyphicon-ok"></i></span>, "success")
            setTimeout(function () {
                $(".rct-message.rct-message-success").children('button').eq(0).click();
            },1000)
        }
    });
},false);

