var reduxStore={roles:['read']}  //redux-store

function log(target, name, descriptor) {  
  var oldValue = descriptor.value;
  descriptor.value = function() {
    console.log(`"Call ${name}" arguments`, arguments);
    return oldValue.apply(null, arguments);
  };
  return descriptor;
}

function checkRole(role){  //  
    return (target, property, descriptor)=>{
        if(reduxStore.roles.indexOf(role)==-1){
            descriptor.value=function(){
                console.log('')
            };  //
        }
        return descriptor
    }
}
class Components{}

class AComponents extends Components{  
  constructor(){
    super()
  }
  @log
  log(arg){
    console.log('log')
  }

  @checkRole('read')  //readread()
  read(){
   console.log('read')
  }

  @checkRole('write')  //writeread()
  write(){
   console.log('write')
  }
  render(){}
}
var a = new AComponents();  
a.read();  
a.write();  
a.log('arg')  


