class Solution {
public:
	TreeNode* sortedListToBST(ListNode* head) {
		int len = 0;
		ListNode* temp = head;
		while (temp != NULL) {
			len++;
			temp = temp->next;
		}
		TreeNode* tree = buildTree(head, len);
		return tree;
	}
	TreeNode* buildTree(ListNode* &head, int len) {
		TreeNode* root;
		if (len <= 0) {
			return NULL;
		}
		int mid = floor(len / 2);
		TreeNode* left = buildTree(head, mid);
		root = new TreeNode(head->val);
		head = head->next;
		root->left = left;
		root->right = buildTree(head, len - mid - 1);
		return root;
	}
};
