#define Infinity 99999999
class Solution {
public:
	vector<int> findMinHeightTrees(int n, vector<pair<int, int>>& edges) {
		vector<vector<int>> V;
		for (int i = 0;i < n; ++i) {
			vector<int> vec(n, Infinity);
			vec[i] = 0;
			V.push_back(vec);
		}
		for (int i = 0;i < edges.size();++i) {
			V[edges[i].first][edges[i].second] = 1;
			V[edges[i].second][edges[i].first] = 1;
		}
		for (int i = 0;i < n;++i)
			for (int j = 0;j < n;j++)
				for (int k = 0;k < n;k++)
					if (V[j][k]>V[j][i] + V[i][k])
						V[j][k] = V[j][i] + V[i][k];

		int Min = INT_MAX;
		vector<int> res;
		for (int i = 0;i < n;++i) {
			int Max = 0;
			for (int j = 0;j < n;++j) {
				if (Max < V[i][j]) {
					Max = V[i][j];
				}
			}
			//		printf("%d\n", Max);
			if (Min > Max) {
				res.clear();
				res.push_back(i);
				Min = Max;
			}
			else {
				if (Min == Max) {
					res.push_back(i);
				}
			}

		}
		return res;
	}
};
