    var wordBreak = function (s, wordDict) {
        let result={};
        let obj = {};
        wordDict.forEach((x) => {
            obj[x] = x;
        })
        var run =function (s,obj) {
            if(s in result){return result[s];}
            let temp = '';
            let b = false;
            for (var i = 0; i < s.length; i++) {
                temp += s[i];
                if (obj[temp]) {
                    // console.log(1)
                    if (i === s.length - 1) {
                        return true;
                    }
                    b = b || run(s.slice(i + 1), obj)
                    if(b===true){break;}
                }

            }
            result[s]=b;
            return b;
        }
        return run(s,obj);
    };
